import React, { Component } from 'react'
import UserService from '../services/UserService';

class CreateUserComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            // step 2
            id: this.props.match.params.id,
            firstName: '',
            middleName: '',
            lastName: '',
            email: ''
        }
        this.changeFirstNameHandler =
            this.changeFirstNameHandler.bind(this);
        this.changeMiddleNameHandler =
            this.changeMiddleNameHandler.bind(this);
        this.changeLastNameHandler =
            this.changeLastNameHandler.bind(this);
        this.saveOrUpdateUser =
            this.saveOrUpdateUser.bind(this);
    }

    // step 3
    componentDidMount() {

        // step 4
        if (this.state.id === '_add') {
            return
        } else {
            UserService.getUserById(this.state.id).
            then((res) => {
                let user = res.data;
                this.setState({
                    firstName: user.firstName,
                    lastName: user.lastName,
                    email: user.email
                });
            });
        }
    }
    saveOrUpdateUser = (e) => {
        e.preventDefault();
        let user = { firstName: this.state.firstName,middleName: this.state.middleName, lastName:
             this.state.lastName, email: this.state.email };
        console.log('user => ' + JSON.stringify(user));

        // step 5
        if (this.state.id === '_add') {
            UserService.createUser(user).then(res => {
                this.props.history.push('/users');
            });
        } else {
            UserService.
            updateUser(user, this.state.id).then(res => {
                this.props.history.push('/users');
            });
        }
    }

    changeFirstNameHandler = (event) => {
        this.setState({ firstName: event.target.value });
    }
    changeMiddleNameHandler = (event) => {
        this.setState({ middleName: event.target.value });
    }

    changeLastNameHandler = (event) => {
        this.setState({ lastName: event.target.value });
    }

    changeEmailHandler = (event) => {
        this.setState({ email: event.target.value });
    }

    cancel() {
        this.props.history.push('/users');
    }

    getTitle() {
        if (this.state.id === '_add') {
            return <h3 className="text-center">Add User</h3>
        } else {
            return <h3 className="text-center">Update User</h3>
        }
    }
    render() {
        return (
            <div>
                <br></br>
        <div className="container">
           <div className="row">
             <div className="card col-md-6 offset-md-3 offset-md-3">
                            {
                                this.getTitle()
                            }
                            <div className="card-body">
                                <form>
            <div className="form-group">
              <label> First Name: </label>
                <input placeholder="First Name" 
                  name="firstName" className="form-control"
                    value={this.state.firstName} 
                      onChange={this.changeFirstNameHandler} />
                          </div>
            <div className="form-group">
              <label> Middle Name: </label>
                <input placeholder="Middle Name" 
                   name="middleName" className="form-control"
                     value={this.state.lastName} 
                      onChange={this.changeMiddleNameHandler} />
                                    </div>
            <div className="form-group">
              <label> Last Name: </label>
                <input placeholder="Last Name" 
                   name="lastName" className="form-control"
                     value={this.state.lastName} 
                      onChange={this.changeLastNameHandler} />
                                    </div>
            <div className="form-group">
                <label> Email : </label>
                    <input placeholder="Email Address" 
                       name="email" className="form-control"
                        value={this.state.email} 
                         onChange={this.changeEmailHandler} />
                                    </div>
            <div className="form-group">
                <label> Gender : </label>
                  <select class="form-control" name="gender">
                    <option value="" hidden="true" selected="">Please select gender...</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                  </select>
                                    </div>
            
            <div className="form-group">
            <label> Birthday : </label>
                <input className="form-control" placeholder="Birthday" type="date" name="birthday"/>
                </div>

                <div className="form-group">
            <label> Contact no. : </label>
                <input className="form-control" placeholder="Contact number(s)" type="text" name="contact"/>
                </div>

            <div className="form-group">
            <label> Address : </label>
                <input className="form-control" placeholder="Address" type="text" name="address"/>
                </div>

                <div className="form-group">
                <label> Civil Status : </label>
                  <select class="form-control" name="civil_status">
                    <option value="" hidden="true" selected="">Please select your status...</option>
                    <option value="Single">Single</option>
                    <option value="Married">Married</option>
                    <option value="Separated">Separated</option>
                    <option value="Widowed">Widowed</option>
                  </select>
                                    </div>

            
                                    

             <button className="btn btn-success" 
                  onClick={this.saveOrUpdateUser}>Save
                    </button>
             <button className="btn btn-danger" 
                  onClick={this.cancel.bind(this)} 
                     style={{ marginLeft: "10px" }}>Cancel
                        </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default CreateUserComponent
